function permutate(word) {
  var words = [];
  
  if (!word || typeof word !== 'string')
    return 'Invalid input!';

    function recurse(prefix, str) {
        if (str.length === 1) {
            if (words.indexOf(prefix + str) < 0) {
                words.push(prefix + str);
            }
        } else {
            str.split('').forEach(function(letter, index) {
                var substr = str.slice(0, index) + str.slice(index + 1);
                recurse(prefix + letter, substr);
            });
        }
    }

    recurse('', word);
    return words;
}

function intersect_arrays(a, b) {
    var sorted_a = a.sort();
    var sorted_b = b.sort();
    var common = [];
    var a_i = 0;
    var b_i = 0;

    while (a_i < a.length && b_i < b.length)
    {
        if (sorted_a[a_i] === sorted_b[b_i]) {
            common.push(sorted_a[a_i]);
            a_i++;
            b_i++;
        }
        else if(sorted_a[a_i] < sorted_b[b_i]) {
            a_i++;
        }
        else {
            b_i++;
        }
    }
    return common;
}

